import Url from './url';
import Http from './http';
import Headers from './headers';
import Interceptor from './interceptor';
import Interceptors from './interceptors';
import Errors from './errors';
import Serializer from './serializer';
import Serializers from './serializers';
import Providers from './providers';

export {
  Url,
  Http,
  Headers,
  Interceptor,
  Interceptors,
  Errors,
  Serializer,
  Serializers,
  Providers
};

/**
 * Base class to be inherited by Interceptors
 * @class
 * @name Interceptor
 */
export default class Interceptor {
  /**
   * Transforms (or fails) an outgoing request.
   * @method
   * @name request
   * @param {Request} request - The request to be processed
   * @param {Function<Request>} accept - called on success to process a (potentially transformed) {@link Request}
   * @param {Function} reject - called on failure to pass an error
   */
  request(request, accept, reject) {
    accept(request);
  }


  /**
   * Try to recover from a request error so processing can resume.
   * @method
   * @name requestError
   * @param error - The error to be processed
   * @param {Function} accept - called on success if we were able to recover
   * @param {Function} reject - called on failure to pass an error
   */
  requestError(error, accept, reject) {
    reject(error);
  }

  /**
   * @method
   * @name response
   * @param {Response} response - The response to be processed
   * @param {Function<Response>} accept - called on success to process a (potentially transformed) {@link Response}
   * @param {Function} reject - called on failure to pass an error
   */
  response(response, accept, reject) {
    accept(response);
  }

  /**
   * Try to recover from a response error so processing can resume.
   * @method
   * @name responseError
   * @param error - The error to be processed
   * @param {Function} accept - called on success if we were able to recover
   * @param {Function} reject - called on failure to pass an error
   */
  responseError(error, accept, reject) {
    reject(error);
  }
}

import { isObject } from 'lodash';
import { isFile, isFormData, isBlob } from '../utilities';
import Interceptor from '../interceptor';

/**
 * Tries to automatically detect the response content type and deserialize the
 * raw body into the appropriate type.
 * @class
 * @name BodyTransformer
 */
export default class BodyTransformer extends Interceptor {
  /**
   * Transforms (or fails) an outgoing request.
   * @method
   * @name request
   * @param {Request} request - The request to be processed
   * @param {Function<Request>} accept - called on success to process a (potentially transformed) {@link Request}
   * @param {Function} reject - called on failure to pass an error
   */
  request(request, accept, reject) {
    const body = request.body();

    if (!!body) {
      const serializer = request.serializer();
      const contentType = request.contentType();

      request
        .contentType(contentType || serializer.contentType)
        .body(serializer.serialize(body));
    }

    accept(request);
  }
}

export let bodyTransformer = new BodyTransformer();

import Interceptor from '../interceptor';

/**
 * Rejects a response with a non-20x status-code.
 * @class
 * @name ErrorHandling
 */
export default class ErrorHandling extends Interceptor {
  /**
   * @method
   * @name response
   * @param {Response} response - The response to be processed
   * @param {Function<Response>} accept - called on success to process a (potentially transformed) {@link Response}
   * @param {Function} reject - called on failure to pass an error
   */
  response(response, accept, reject) {
    if (Math.floor(response.status() / 100) === 2) {
      accept(response);
    } else {
      reject(response);
    }
  }
}

export let errorHandling = new ErrorHandling();

import { methodOverride } from './method-override';
import { bodyTransformer } from './body-transformer';
import { xsrf } from './xsrf';
import { errorHandling } from './error-handling';

export default {
  MethodOverride: methodOverride,
  BodyTransformer: bodyTransformer,
  XSRF: xsrf,
  ErrorHandling: errorHandling
};

import Interceptor from '../interceptor';

const BROWSER_METHODS = ['GET', 'POST'];

/**
 * If the HTTP verb is not one understood by Browsers, change the verb
 * to a POST and send the X-HTTP-Method-Override header with the original
 * method.  This should generally be understood by the routers of most
 * server backends.
 * @class
 * @name MethodOverride
 */
export default class MethodOverride extends Interceptor {
  /**
   * Transforms (or fails) an outgoing request.
   * @method
   * @name request
   * @param {Request} request - The request to be processed
   * @param {Function<Request>} accept - called on success to process a (potentially transformed) {@link Request}
   * @param {Function} reject - called on failure to pass an error
   */
  request (request, accept, reject) {
    const originalMethod = request.method();

    if (!BROWSER_METHODS.some(m => m === originalMethod)) {
      request
        .method('POST')
        .headers('X-HTTP-Method-Override', originalMethod);
    }

    accept(request);
  }
}

export let methodOverride = new MethodOverride();

import { isUndefined, isString } from 'lodash';
import Cookies from 'js-cookie';
import Interceptor from '../interceptor';

/**
 * Reads the value from the configured XSRF cookie and sends it back to the
 * server and sends it back in the configured header.
 * @class
 * @name XSRF
 */
export default class XSRF extends Interceptor {
  /**
   * Transforms (or fails) an outgoing request.
   * @method
   * @name request
   * @param {Request} request - The request to be processed
   * @param {Function<Request>} accept - called on success to process a (potentially transformed) {@link Request}
   * @param {Function} reject - called on failure to pass an error
   */
  request(request, accept, reject) {
    const xsrfToken = Cookies.get(request.xsrfCookieName());
    const xsrfHeader = request.xsrfHeaderName();

    if (!isUndefined(xsrfToken) && isString(xsrfToken)) {
      request.headers(xsrfHeader, xsrfToken);
    }

    accept(request);
  }
}

export let xsrf = new XSRF();

import { reduce, startsWith, endsWith } from 'lodash';

export default {
  separator: '/',

  join() {
    const separator = this.separator;
    const initial = arguments[0];
    const args = [...arguments].slice(1)

    let joined = reduce(args, (accum, value) => {
      if (value.trim() != '') {
        if (endsWith(accum, separator) && startsWith(value, separator)) {
          accum += value.substring(1);
        } else if (endsWith(accum, separator) || startsWith(value, separator)) {
          accum += value;
        } else {
          accum += '/' + value;
        }
      }

      return accum;
    }, initial);

    while (endsWith(joined, separator)) {
      joined = joined.slice(0, -1);
    }

    return joined;
  }
};

import XHRProvider from './xhr';

const provider = (() => {
  // TODO: Implement FetchProvider and use feature-detection to determine which to use
  return XHRProvider;
})();

export default provider;

import { partial, isInteger } from 'lodash';
import Rx from 'rxjs';
import XHRBuilder from './xhr-builder';
import Response from '../../../response';
import RequestInterceptorChain from '../../../request-interceptor-chain';
import ResponseInterceptorChain from '../../../response-interceptor-chain';

/** @function
 * Provider-implementation for browser-based clients.  Providers are simply
 * functions that take in a {@link Request} and return an RxJS Observable
 * that eventually contains the {@link Response}.
 * @name XHRProvider
 * @param {Request} request
 * @returns {Observable<Response>}
 */
export default function XHRProvider(request) {
  const interceptors = request.interceptors();

  function attempt(observable) {
    let response;
    let offset = 0;
    let body = new Rx.Subject();
    let uploadProgress = new Rx.Subject();
    let downloadProgress = new Rx.Subject();
    let all = [observable, body, uploadProgress, downloadProgress];

    function errorAll(err) {
      all.forEach(o => o.error(err));
    }

    function completeAll() {
      all.forEach(o => o.complete());
    }

    function nextChunk() {
      let chunk = xhr.responseText.slice(offset);
      offset = xhr.responseText.length;
      body.next(chunk);
    }

    const xhr = new XHRBuilder()
      .request(request)
      .onHeadersReceived((evt) => {
        response = new Response({
          xhr,
          body,
          uploadProgress,
          downloadProgress
        });

        const responseChain = new ResponseInterceptorChain(
          interceptors,
          (transformedResponse) => observable.next(transformedResponse),
          Rx.Observable.throw
        );

        responseChain.run(response);
      })
      .onUploadProgress((evt) => {
        uploadProgress.next(evt);
      })
      .onDownloadProgress((evt) => {
        downloadProgress.next(evt);
        if (response.isChunked()) {
          nextChunk();
        }
      })
      .onLoad((evt) => {
        if (response.isChunked()) {
          if (xhr.responseText.length > offset) {
            nextChunk();
          }
        } else {
          body.next(xhr.responseText);
        }
      })
      .onError(Rx.Observable.throw)
      .onAbort(errorAll)
      .onLoadEnd(completeAll)
      .build();

    const success = transformed => {
      if (!!transformed.body()) xhr.send(transformed.body());
      else xhr.send();
    };

    const requestChain = new RequestInterceptorChain(
      interceptors,
      success,
      (err) => {
        Rx.Observable.throw(err);
        completeAll();
      }
    );

    requestChain.run(request);
  }

  return Rx.Observable.create(attempt);
}

import { isInteger, partial, noop } from 'lodash';

const STATE = {
  UNSENT: 0,
  OPEN: 1,
  HEADERS_RECEIVED: 2,
  LOADING: 3,
  DONE: 4
}

/**
 * Internal class used to construct and XMLHttpRequest
 * @private
 * @class
 * @name XHRBuilder
 */
export default class XHRBuilder {
  constructor() {
    this.headersReceived = noop;
    this.downloadProgress = noop;
    this.uploadProgress = noop;
    this.complete = noop;
    this.progress = noop;
    this.error = noop;
