    const ui = userInfo() ? `${userInfo()}@` : '';
    const h = host() || '';
    const p = port() ? `:${port()}` : '';

    return pr + ui + h + p;
  };

  const path = this.path = function () {
    let dir = directory() || '';
    let f = file() || '';

    if (endsWith(dir, '/')) {
      if (startsWith(f, '/')) {
        return dir + f.substring(1);
      }

      return dir + f;
    } else if (startsWith(f, '/')) {
