    }

    return `${dir}/${f}`;
  };

  this.merge = function(other) {
    const copied = cloneDeep(parts);
    const otherParts = parseUri(other.toString());
    const propertiesToMerge = ['directory', 'file', 'fragment', 'path', 'query'];
    propertiesToMerge.forEach((property) => {
