import { isInteger, partial, noop } from 'lodash';
import { RequestInterceptorChain } from './interceptors';

const STATE = {
  UNSENT: 0,
  OPEN: 1,
  HEADERS_RECEIVED: 2,
  LOADING: 3,
  DONE: 4
}

export default class XHRBuilder {
  constructor() {
    this.headersReceived = noop;
    this.downloadProgress = noop;
    this.uploadProgress = noop;
    this.complete = noop;
    this.progress = noop;
    this.error = noop;
    this.abort = noop;
    this.load = noop;
    this.loadEnd = noop;
  }

  onHeadersReceived(fn) {
    this.headersReceived = fn;
    return this;
  }

  onDownloadProgress(fn) {
    this.downloadProgress = fn;
    return this;
  }

  onUploadProgress(fn) {
    this.uploadProgress = fn;
    return this;
  }

  onError(fn) {
    this.error = fn;
    return this;
  }

  onAbort(fn) {
    this.abort = fn;
    return this;
  }

  onLoad(fn) {
    this.load = fn;
    return this;
  }

  onLoadEnd(fn) {
    this.loadEnd = fn;
    return this;
  }

  request(req) {
    this.req = req;
    return this;
  }

  build() {
    const self = this;
    const request = this.req;
    const xhr = new XMLHttpRequest();

    const openArgs = [request.method(), request.url().toString(), true];
    if (request.username()) {
      openArgs.push(request.username());

      if (request.password()) {
        openArgs.push(request.password());
      }
    }

    xhr.open(...openArgs);

    if (isInteger(request)) {
      xhr.timeout = request.timeout();
    }

    const headers = request.headers();

    Object.keys(headers).forEach(headerName => {
      xhr.setRequestHeader(headerName, headers[headerName]);
    });

    if (xhr.upload) {
      xhr.upload.addEventListener('progress', self.uploadProgress);
      xhr.upload.addEventListener('error', self.error);
      xhr.upload.addEventListener('abort', self.abort);
    }

    xhr.addEventListener('progress', self.downloadProgress);
    xhr.addEventListener('error', self.error);
    xhr.addEventListener('abort', self.abort);
    xhr.addEventListener('load', self.load);
    xhr.addEventListener('loadend', self.loadEnd);

    xhr.addEventListener('readystatechange', (evt) => {
      if (xhr.readyState === STATE.HEADERS_RECEIVED) {
        self.headersReceived(evt);
      }
    });

    return xhr;
  }
}

import { partial, isInteger } from 'lodash';
import Rx from 'rxjs';
import XHRBuilder from './xhr-builder';
import Response from './response';
import { RequestInterceptorChain, ResponseInterceptorChain } from './interceptors';

export default function XHRProvider(request) {
  const interceptors = request.interceptors();

  function attempt(observable, retries = request.retries()) {
    let response;
    let offset = 0;
    let body = new Rx.Subject();
    let uploadProgress = new Rx.Subject();
    let downloadProgress = new Rx.Subject();
    let all = [observable, body, uploadProgress, downloadProgress];

    function errorAll(err) {
      all.forEach(o => o.error(err));
    }

    function completeAll() {
      all.forEach(o => o.complete());
    }

    function exceptionHandler(evt) {
      if (retries > 0) attempt(observable, retries - 1);
      else errorAll(evt);
    }

    function nextChunk() {
      let chunk = xhr.responseText.slice(offset);
      offset = xhr.responseText.length;
      body.next(chunk);
    }

    const xhr = new XHRBuilder()
      .request(request)
      .onHeadersReceived((evt) => {
        response = new Response(
          xhr,
          body,
          uploadProgress,
          downloadProgress
        );

        const responseChain = new ResponseInterceptorChain(
          interceptors,
          (transformedResponse) => observable.next(transformedResponse),
          exceptionHandler
        );

        responseChain.run(response);
      })
      .onUploadProgress((evt) => {
        uploadProgress.next(evt);
      })
      .onDownloadProgress((evt) => {
        downloadProgress.next(evt);
        if (response.isChunked()) {
          nextChunk();
        }
      })
      .onLoad((evt) => {
        if (response.isChunked()) {
          if (xhr.responseText.length > offset) {
            nextChunk();
          }
        } else {
          body.next(xhr.responseText);
        }
      })
      .onError(exceptionHandler)
      .onAbort(exceptionHandler)
      .onLoadEnd(completeAll)
      .build();

    const success = transformed => {
      if (!!transformed.body()) xhr.send(transformed.body());
      else xhr.send();
    };

    const requestChain = new RequestInterceptorChain(
      interceptors,
      success,
      (err) => {
        observable.error(err);
        completeAll();
      }
    );

    requestChain.run(request);
  }

  return Rx.Observable.create(attempt);
}
